import React from 'react';
import { RefreshControl, Pressable, StatusBar, SafeAreaView, ScrollView, StyleSheet, Text, TextInput, View, Image  } from 'react-native';

class Gare extends React.Component  {
  constructor(props) {
		super(props);
    this.state = {
        query : '',
        stops: [],
        onload: false,
    }
    this.onChangeText = this.onChangeText.bind(this)
	}

  onChangeText(e){
    this.setState({
      query: e,
      onload: true
    })
    this.getStops();
  }

  getStops() {
    const query = this.state.query;
		let url = 'https://api.mylines.fr/sncf/search?q=' + query;

		fetch(url, {
			method: 'get'
		})
		.then(res => res.json())
		.then(data => {
			this.setState({
				stops: data.stop_points,
				onload: false
			});
		})
		.catch(err => {
			this.setState({ onload: false });
		});
	}

	render(){
		return (
      <SafeAreaView  style={styles.container}>          
          <View>  
            <Text style={styles.title}>Rechercher une gare</Text>
            <View style={styles.form}>
              <TextInput
                style={styles.input}
                onChangeText={this.onChangeText}
                value={this.state.query}
                autoFocus = {true}

                placeholder = 'Rechercher une gare'
                placeholderTextColor = '#ccc'
              />
            </View> 

            <Search></Search>    

          </View>
      </SafeAreaView >
    );
  }
}

class Search extends React.Component {
	render(){
		let opt = this.props.opt;

		return (
			<>
				<div className="gui">
					{this.props.displayForce == false ? <div onClick={this.props.show} className="close">Fermer</div> : <></>}					
						<br/>
					<input type="text" placeholder="Rechercher une autre gare" ref={inputEl => (this.searchInput = inputEl)} onChange={this.handleChange} onKeyDown={this.handleKey} autocomplete="off" /> 
					{this.state.load ? <div> <div className="progress-bck"></div> <div className="progress indeterminate"></div></div> : <></>}
						<br />
					<div className="stopList"> 

						{this.state.stops.map((stop, i) => (
							<GuiStop 
								key = {i}
								stop = {stop}
								onover = {i == this.state.curent ? true : false}
								auth = {this.props.auth}
								opt = {this.state.opt}
								show = {this.props.show}
							/>
						))}
						{this.state.stops.length == 0 ? <div className="center">Aucun r√©sultat</div> : <></>}
					</div>
				</div>
			</>
		);
	}
}

class Stop extends React.Component {
	render(){		
		let stop = this.props.stop;
		let name = stop.stop_point.name;
		let id = stop.stop_point.uic_code;
		let onover = this.props.onover;

		let url;
		if (window.location.href.indexOf('mylines.fr/embed') >= 0)
            url = '/embed/' + this.props.opt + '/' + this.props.auth + '/'
		else	
			url = '/' + this.props.opt + '/'

		return (
			<Link to={url + id + '?gui'} className={onover == true ? 'overmouse2' : 'overmouse'} onClick={this.props.show}>
				<div>
				{name}
				</div>
			</Link>
		);
	}
}


export default Gare;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#202020',
  },
  title: {
    fontSize: 30,
    textAlign: 'left',
    fontWeight: 'bold',
    color: '#fff',
    margin: 23,
  },
  form: {
    borderColor: '#0064aab7',
    borderWidth: 2,
    borderRadius: 10,
    marginTop: 15,
    marginLeft: 15,
    padding: 5,
    color: '#fff',
    fontSize: 20,
    width: '92%',
  },
  input: {
    color: '#fff',
  }
});
