import React from 'react';
import { RefreshControl, Dimensions, Pressable, StatusBar, SafeAreaView, ScrollView, StyleSheet, Text, View, Image  } from 'react-native';

class Error extends React.Component  {
	render(){
    const dimensions = Dimensions.get('window');
    const imageHeight = Math.round(dimensions.width * 9 / 16);
    const imageWidth = dimensions.width;

		return (
      <SafeAreaView>          
          <View>      
            <Image style={{width: imageWidth }} source={require('./assets/img/maintenance.png')}></Image>
            <Text style={styles.title}>{this.props.error}</Text>
            <Text>{this.props.error_message}</Text>
          </View>
      </SafeAreaView >
    );
  }
}

export default Error;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#191c2b',
  },
  title: {
    fontSize: 35,
    textAlign: 'center',
    fontWeight: 'bold',
    color: '#fff',
    margin: 23,
  }
});
